	</div><script> jQuery(".Imp__up").on("click", function(e){
		var $el =  jQuery(e.currentTarget);
		var elOffset = $el.offset();
		jQuery("html, body").stop().animate({
			scrollTop: 0
			},
			{
				duration: elOffset.top / 10
			}
			);
		});
		var visible = false;
		jQuery(window).on("scroll", function(e) {

            if( window.scrollY < window.outerHeight && visible ){ 
            	jQuery(".Imp__up").stop().animate({opacity: 0},
            		{
            			complete: function(){
            				visible = false;
							jQuery(this).css({"pointer-events":"none"})
            			}
            		}
            	);
            }else if(!visible && window.scrollY > window.outerHeight){
            	jQuery(".Imp__up").stop().animate({opacity: 1},
            		{
            			complete: function(){
            				visible = true;
            				jQuery(this).css({"pointer-events":"all"})
            			}
            		}
            	)
            }
        })
	 </script>
